mapper.select:
select id
from t_notice
WHERE sender_id = #{senderId}
  AND receiver_id = #{receiverId}
  AND type = #{type}
  AND target_id = #{targetId}

mapper.update:
UPDATE t_notice
SET is_deleted = 1
WHERE sender_id = #{senderId}
  AND receiver_id = #{receiverId}
  AND type = #{type}
  AND target_id = #{targetId}

mapper.select:
SELECT * FROM t_notice WHERE id IN
#{id}
AND is_deleted = 0
